import React from 'react';
import Slider from "react-slick";

const Carrousel = () => {

  const setting ={
    dots: false,
    infinite:true,
    autoplay:true,
    speed:500
  }

  return (
    <div
      className="carrousel_wrapper"
      style={{
        height:`${window.innerHeight}px`,
        overflow:'hidden'
      }}
    >
      <Slider {...setting}>
        <div>
          image 1
        </div>
        <div>
          image 2
        </div>
        <div>
          image 3
        </div>
      </Slider>

    </div>
  );
};

export default Carrousel;
