import React, { Component } from 'react';
import MyButton from '../usb/button';
import Zoom from 'react-reveal/Zoom';

class Pricing extends Component {

  state = {
    prices:[100,200,300],
    positions:['Silver', 'Gold', 'Diamond'],
    desc:[
      'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.',
      'Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.',
      'Sed ut perspiciatis unde omnis iste natus error sit voluptatem  et quasi architecto beatae vitae dicta sunt explicabo. '
    ],
    linkto:['http://google.com', 'http://google.com', 'http://google.com'],
    delay:[500,0,500]
  }

  showBoxes = () => (
    this.state.prices.map((box,i)=>(
      <Zoom delay={this.state.delay[i]} key={i}>
        <div className="pricing_item">
          <div className="pricing_inner_wrapper">
              <div className="pricing_title">
                <span>${this.state.prices[i]}</span>
                <span>${this.state.positions[i]}</span>
              </div>
              <div className="pricing_description">
                {this.state.desc[i]}
              </div>
              <div className="pricing_buttons">
                <MyButton
                  text="Purchase"
                  bck="#ffa800"
                  color="#ffffff"
                  link={this.state.linkto[i]}
                />
              </div>
          </div>
        </div>
      </Zoom>

    )
  )
)

  render() {
    return (
      <div className="bck_black">
        <div className="center_wrapper pricing_section">
          <h2>Pricing</h2>

          <div className="pricing_wrapper">
            {this.showBoxes()}
          </div>

        </div>
      </div>
    );
  }
}

export default Pricing;
